    private class func cidadesSaoPaulo() -> APICidades{
        var cidades = [Cidade]()
        
        cidades.append(Cidade(nome: "São Paulo", descricao: "...", nomeImagem: "..."))
        cidades.append(Cidade(nome: "Santos", descricao: "...", nomeImagem: "..."))
        cidades.append(Cidade(nome: "Osasco", descricao: "...", nomeImagem: "..."))
        // ...
        
        return APICidades(estado: "São Paulo", cidades: cidades)
        
    }
    
}
